import { setBorderRadius } from './border-radius';
export const setOverflowHidden = ({ ctx, rect, borderRadius, overflowHidden, computedStyle, backgroundClip, }) => {
    if (!overflowHidden) {
        return () => { };
    }
    return setBorderRadius({
        ctx,
        rect,
        borderRadius,
        forceClipEvenWhenZero: true,
        computedStyle,
        backgroundClip,
    });
};
