export const setTransform = ({ ctx, transform, parentRect, }) => {
    const offsetMatrix = new DOMMatrix()
        .translate(-parentRect.x, -parentRect.y)
        .multiply(transform)
        .translate(parentRect.x, parentRect.y);
    ctx.setTransform(offsetMatrix);
    return () => {
        ctx.setTransform(new DOMMatrix());
    };
};
